      'aliases' => 
      array (
      ),
      'reference' => '9e0def3a78f8ace8ba9b288374d55526ecd480a0',
    ),
    'symfony/deprecation-contracts' => 
    array (
      'pretty_version' => 'v2.2.0',
      'version' => '2.2.0.0',
      'aliases' => 
